import React from 'react'
import './GetUser.css'
const URL_PAGE1='https://reqres.in/api/users?page=1'
const URL_PAGE2='https://reqres.in/api/users?page=2'
async function getUsers(url){
   fetch(url).then(res=>res.json()).then(data=>{
    let users=data.data;
    users.forEach(function(user){
      let UsersContainer=document.getElementById('GetUsersCards');
      let userCard=document.createElement('div');
      let img=document.createElement('img');
      let firstName=document.createElement('span')
      let lastName=document.createElement('span')
      let email=document.createElement('span')
      img.setAttribute('src',user.avatar);
      img.classList.add('circleImg')
      userCard.classList.add('card');
      firstName.textContent=user.first_name;
      lastName.textContent=user.last_name;
      email.textContent=user.email;
      userCard.appendChild(img)
      userCard.appendChild(firstName)
      userCard.appendChild(lastName)
      userCard.appendChild(email)
      UsersContainer.appendChild(userCard)
    })
   })
}
const GetUsers = () => {
  return (
    <div>
      <div className='header'>
      <img src='' alt='logo'></img>
       <ul>
        <li>Home</li>
        <li>Home</li>
        <li>Home</li>
        <li>Home</li>
       </ul>
       <button id='getUsers' onClick={getUsers(URL_PAGE1)} >Get Users</button>
    </div>
    <div className='GetUsersCards' id='GetUsersCards'>
       
    </div>
    </div>
  )
}

export default GetUsers
